INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (1, 'Cent`anni di solitudine', 'Narrativa', 'uploads/copertine/centoanni.png', 1967, '978-0-7475-3274-2', 'Molti anni dopo, davanti al plotone di esecuzione, il colonnello Aureliano Buendía avrebbe ricordato quel pomeriggio remoto in cui suo padre l`aveva portato a conoscere il ghiaccio. Macondo era allora un villaggio di venti case di fango e canne costruite sulla riva di un fiume dalle acque diafane che si precipitavano su un letto di pietre levigate, bianche ed enormi come uova preistoriche. Il mondo era così recente che molte cose erano senza nome, e per menzionarle bisognava indicarle col dito.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (2, 'La pietra filosofale', 'Fantasy', 'uploads/copertine/pietra.png', 1997, '378-0-7475-3274-2', 'Harry Potter è un ragazzo orfano che vive con i suoi zii, i Dursley, che lo maltrattano. La sua vita cambia radicalmente quando riceve una lettera da Hogwarts, una scuola di magia. Qui scopre di essere un mago e di avere un destino speciale. Insieme ai suoi nuovi amici, Ron e Hermione, Harry affronta avventure incredibili, scopre i segreti del mondo magico e si confronta con il temuto Lord Voldemort, il mago oscuro che ha ucciso i suoi genitori. La storia esplora temi di amicizia, coraggio e la lotta tra il bene e il male, mentre Harry inizia il suo viaggio per diventare un vero mago.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (3, '1984', 'Distopia', 'uploads/copertine/1984.png',  1949, '978-0-7375-1334-2', 'Ambientato in un futuro distopico, "1984" racconta la storia di Winston Smith, un impiegato del Partito che vive sotto un regime totalitario che controlla ogni aspetto della vita. La società è sorvegliata dal Grande Fratello, e il pensiero indipendente è punito. Winston inizia a ribellarsi contro il sistema, cercando la verità e l`amore in un mondo di oppressione. La narrazione esplora temi di libertà, verità e la manipolazione della realtà, offrendo una critica profonda alla società e alla politica.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (4, 'Il buio oltre la siepe', 'Romanzo', 'uploads/copertine/buio.png', 1960, '971-0-7475-3124-2', 'Ambientato negli anni `30 in una cittadina del sud degli Stati Uniti, "Il buio oltre la siepe" narra la storia di Scout Finch, una giovane ragazza che cresce in un contesto di razzismo e ingiustizia. Attraverso gli occhi di Scout, seguiamo le vicende del padre, Atticus Finch, un avvocato che difende un uomo di colore accusato ingiustamente di stupro. La storia affronta temi di moralità, empatia e la perdita dell`innocenza, mentre Scout e suo fratello Jem imparano le dure verità della vita.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (5, 'Il grande Gatsby', 'Romanzo', 'uploads/copertine/big.png', 1925, '978-1-7275-1224-2', 'Ambientato negli anni `20, "Il grande Gatsby" racconta la storia di Jay Gatsby, un misterioso miliardario noto per le sue sontuose feste. Attraverso gli occhi di Nick Carraway, il narratore, esploriamo il sogno americano e le sue illusioni. Gatsby è innamorato di Daisy Buchanan, una donna sposata, e la sua ossessione per lei lo porta a una tragica fine. Il romanzo esplora temi di amore, ambizione e la disillusione del sogno americano, offrendo una critica alla società dell`epoca.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (6, 'Le avventure di Tom Sawyer', 'Romanzo', 'uploads/copertine/tom.png', 1876, '938-6-5473-3214-1', 'Le avventure di Tom Sawyer seguono le disavventure di un giovane ragazzo vivace e intraprendente che vive lungo il fiume Mississippi. Tom è noto per le sue marachelle e la sua immaginazione vivace. Insieme al suo amico Huck Finn, vive avventure emozionanti, dalla fuga su un`isola deserta alla scoperta di un tesoro. Il romanzo esplora temi di libertà, amicizia e crescita, offrendo uno sguardo nostalgico sull`infanzia e la vita americana del XIX secolo.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (7, 'Orgoglio e pregiudizio', 'Romanzo', 'uploads/copertine/orgoglio.png', 1813, '978-0-7475-3274-2', 'Ambientato nell`Inghilterra del XIX secolo, "Orgoglio e pregiudizio" segue la storia di Elizabeth Bennet, una giovane donna intelligente e indipendente. La narrazione esplora le dinamiche sociali e le convenzioni dell`epoca, in particolare attraverso il rapporto tra Elizabeth e il misterioso Mr. Darcy. Tra malintesi, orgoglio e pregiudizi, i due protagonisti devono affrontare le loro differenze e le aspettative della società, in un viaggio che li porterà a scoprire il vero significato dell`amore e del rispetto reciproco.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (8, 'Il vecchio e il mare', 'Romanzo', 'uploads/copertine/mare.png', 1952, '978-0-7475-3274-2', '"Il vecchio e il mare" racconta la storia di Santiago, un pescatore cubano anziano che affronta una lunga battaglia contro un gigantesco marlin. Dopo 84 giorni senza catture, Santiago si avventura lontano nel mare aperto, dove incontra il pesce che diventa il suo avversario. La storia esplora temi di perseveranza, dignità e la lotta dell`uomo contro la natura, offrendo una profonda riflessione sulla vita e sulla resilienza umana.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (9, 'Guerra e pace', 'Romanzo', 'uploads/copertine/pace.png', 1869, '978-0-7475-3274-2', 'Considerato uno dei capolavori della letteratura mondiale, "Guerra e pace" narra le vicende di diverse famiglie aristocratiche russe durante le guerre napoleoniche. Attraverso le storie di Pierre Bezukhov, Natasha Rostova e Andrei Bolkonsky, Tolstoj esplora temi di amore, destino, e la ricerca di significato in un mondo in tumulto. Il romanzo offre una profonda analisi della società russa e delle sue trasformazioni, mescolando storia e fiction in un affresco epico.');
INSERT INTO libro (id, titolo, genere, copertina, anno_pubblicazione, isbn, descrizione) VALUES (10, 'La signora Dalloway', 'Modernismo', 'uploads/copertine/signora.png',  1925, '978-0-5321-3274-2', 'Ambientato nella Londra del dopoguerra, "La signora Dalloway" segue Clarissa Dalloway mentre prepara una festa per la sera. Attraverso un flusso di coscienza, il romanzo esplora i pensieri e le emozioni di Clarissa, così come quelli di altri personaggi, tra cui Septimus Warren Smith, un veterano traumatizzato dalla guerra. Virginia Woolf affronta temi di identità, tempo e la complessità delle relazioni umane, offrendo uno sguardo profondo sulla vita interiore dei suoi personaggi.');
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (1, 'Un capolavoro di realismo magico', 'Un capolavoro di realismo magico che narra la saga della famiglia Buendía in un mondo di eventi straordinari e malinconici.', 5, 1, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (2, 'Magia e avventura', 'Un inizio fantastico per la saga di Harry Potter, pieno di magia, amicizia e avventure indimenticabili.', 5, 2, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (3, 'Un avvertimento potente', 'Un potente avvertimento contro il totalitarismo, che esplora la sorveglianza e la manipolazione della verità.', 5, 3, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (4, 'Una storia toccante', 'Una storia toccante che affronta il razzismo e l`ingiustizia attraverso gli occhi di una giovane ragazza.', 5, 4, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (5, 'Critica al sogno americano', 'Un `analisi affascinante del sogno americano, esplorando l`amore, l`ossessione e la disillusione.', 4, 5, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (6, 'Nostalgia dell`infanzia', 'Un racconto nostalgico dell`infanzia, pieno di avventure e marachelle.', 4, 6, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (7, 'Amore e convenzioni', 'Una brillante satira sociale che esplora l`amore e le convenzioni dell`epoca.', 5, 7, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (8, 'Resilienza e dignità', 'Una storia di resilienza e lotta, che racconta la battaglia di un pescatore contro un gigantesco marlin.', 5, 8, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (9, 'Epopea storica', 'Un`epopea storica che intreccia le vite di diverse famiglie durante le guerre napoleoniche.', 5, 9, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (10, 'Un viaggio interiore', 'Un romanzo innovativo che esplora la vita interiore di Clarissa Dalloway in un giorno di preparativi per una festa.', 4, 10, 1);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (11, 'Un viaggio epico', 'Un viaggio epico attraverso la storia e la magia di Macondo, ricco di simbolismo e profondità.', 5, 1, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (12, 'Un classico della fantasia', 'Un classico della letteratura fantasy che cattura l`immaginazione e il cuore di ogni lettore.', 5, 2, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (13, 'Un racconto inquietante', 'Un racconto inquietante che fa riflettere sulla libertà e il controllo nella società moderna.', 5, 3, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (14, 'Lezioni di vita', 'Un libro che offre lezioni di vita importanti attraverso la prospettiva innocente di una bambina.', 5, 4, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (15, 'Sogni infranti', 'Una storia di sogni infranti e illusioni, che esplora la superficialità della società degli anni `20.', 4, 5, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (16, 'Avventure senza tempo', 'Un racconto di avventure senza tempo che celebra l`innocenza e la curiosità dell`infanzia.', 4, 6, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (17, 'Amore e ironia', 'Un romanzo che combina amore e ironia, esplorando le complessità delle relazioni umane.', 5, 7, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (18, 'Una lotta epica', 'Una lotta epica tra uomo e natura, che mette in luce la determinazione e la resilienza umana.', 5, 8, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (19, 'Un affresco della storia', 'Un affresco della storia russa, che intreccia amore, guerra e filosofia in un racconto straordinario.', 5, 9, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (20, 'Un flusso di coscienza', 'Un flusso di coscienza che esplora la complessità della mente umana e le relazioni interpersonali.', 4, 10, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (21, 'Sogni di libertà', 'Un racconto che esplora il desiderio di libertà e le sfide che si affrontano per ottenerla.', 4, 8, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (22, 'Il mistero del passato', 'Un thriller avvincente che svela segreti sepolti e misteri irrisolti.', 5, 8, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (23, 'Viaggio nel tempo', 'Un romanzo che porta il lettore in un viaggio attraverso epoche e luoghi diversi, ricco di avventure.', 4, 10, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (24, 'La forza dell’amicizia', 'Una storia commovente che celebra il potere dell’amicizia e della solidarietà.', 5, 9, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (25, 'Riflessioni sulla vita', 'Un saggio che invita a riflettere sulle scelte e le esperienze che plasmano la nostra esistenza.', 4, 8, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (26, 'Ombre e luci', 'Un romanzo che esplora le sfide della vita, tra momenti di oscurità e speranza.', 4, 4, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (27, 'Il canto della natura', 'Una celebrazione della bellezza della natura e della sua importanza nella vita umana.', 5, 5, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (28, 'La danza del destino', 'Una storia avvincente che intreccia le vite di diversi personaggi in un destino comune.', 4, 5, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (29, 'Sussurri nel vento', 'Un racconto poetico che esplora i legami tra le persone e la natura.', 5, 9, 2);
INSERT INTO recensione (id, titolo, testo, voto, libro_id, utente_id) VALUES (30, 'Il viaggio dell’anima', 'Un romanzo che invita a riflettere sul significato della vita e del nostro percorso personale.', 4, 9, 2);
